from fastapi import APIRouter, Depends, HTTPException
from supabase import Client
from app.schemas.user import UserCreate
from app.supabase_client import get_supabase_client

router = APIRouter()

@router.post("/register", status_code=201)
def create_user(user: UserCreate, supabase: Client = Depends(get_supabase_client)):
    """
    Create a new user.
    """
    try:
        response = supabase.auth.sign_up({
            "email": user.email,
            "password": user.password,
        })
        # The sign_up response does not throw an error for existing users,
        # so we need to check the response object.
        if response.user and response.user.identities == []:
             # This indicates the user already exists but is unconfirmed.
             # Supabase sends a confirmation email again in this case.
             return {"message": "User already exists but is unconfirmed. A new confirmation email has been sent."}
        if not response.user:
            # A more robust check might be needed depending on Supabase API changes
            # but for now, no user object suggests an issue.
            # A common issue is the password being too weak.
            if "Password should be at least 6 characters" in str(response): # A bit brittle
                 raise HTTPException(status_code=422, detail="Password should be at least 6 characters.")
            raise HTTPException(status_code=400, detail="Could not create user.")

    except Exception as e:
        # Catching cases where the user might already exist and is confirmed
        if "User already registered" in str(e):
            raise HTTPException(status_code=409, detail="A user with this email already exists.")
        raise HTTPException(status_code=500, detail=str(e))

    return {"message": "User created successfully. Please check your email for verification."}

