import RegisterPage from '@/app/auth/register/page';
import { useRouter } from 'next/navigation';

// Mock the useRouter hook
jest.mock('next/navigation', () => ({
  useRouter: jest.fn(),
}));

describe('RegisterPage', () => {
  const mockRouter = {
    push: jest.fn(),
  };
  (useRouter as jest.Mock).mockReturnValue(mockRouter);

  let fetchSpy: jest.SpyInstance;

  beforeEach(() => {
    jest.clearAllMocks();
    fetchSpy = jest.spyOn(global, 'fetch');
  });

  afterEach(() => {
    fetchSpy.mockRestore();
  });

it('submits the form successfully and redirects', async () => {
  fetchSpy.mockResolvedValueOnce({
    ok: true,
    json: async () => ({ message: 'Success' }),
  });

  render(<RegisterPage />);
  fireEvent.change(screen.getByLabelText(/email/i), { target: { value: 'test@example.com' } });
  fireEvent.change(screen.getByLabelText('Password'), { target: { value: 'password123' } });
  fireEvent.change(screen.getByLabelText('Confirm Password'), { target: { value: 'password123' } });
  fireEvent.click(screen.getByRole('button', { name: /create account/i }));

  await waitFor(() => {
    expect(fetchSpy).toHaveBeenCalledWith('http://localhost/api/v1/auth/register', expect.any(Object));
    expect(mockRouter.push).toHaveBeenCalledWith('/auth/check-email');
  });
});

it('shows an error message on failed submission', async () => {
  fetchSpy.mockResolvedValueOnce({
    ok: false,
    json: async () => ({ detail: 'Registration failed' }),
  });

  render(<RegisterPage />);
  fireEvent.change(screen.getByLabelText(/email/i), { target: { value: 'fail@example.com' } });
  fireEvent.change(screen.getByLabelText('Password'), { target: { value: 'password123' } });
  fireEvent.change(screen.getByLabelText('Confirm Password'), { target: { value: 'password123' } });
  fireEvent.click(screen.getByRole('button', { name: /create account/i }));

  expect(await screen.findByText(/registration failed/i)).toBeInTheDocument();
  expect(fetchSpy).toHaveBeenCalledWith('http://localhost/api/v1/auth/register', expect.any(Object));
});

