import { createServerClient } from '@/utils/supabase'
import { cookies } from 'next/headers'
import { redirect } from 'next/navigation'
import { getUsageCount, incrementUsageCount, checkUsageLimit } from '@/lib/guest-usage'

export default async function Page() {
  const cookieStore = cookies()
  const supabase = createServerClient(cookieStore)

  const { data: { user } } = await supabase.auth.getUser()

  if (!user) {
    // User is a guest
    if (checkUsageLimit()) {
      redirect('/login'); // Redirect to login/registration if limit reached
    }
    // For guests, we might display a subset of data or a generic message
    // For now, allow to proceed, but increment usage after successful "use"
    // The "use" here is successfully viewing the notes.
    const { data: notes } = await supabase.from('notes').select() // Assuming guest can view some notes

    incrementUsageCount(); // Increment usage after successful operation for guest

    return (
      <>
        <h1>Guest Mode - Limited Access</h1>
        <p>You have {getUsageCount()} out of 2 free uses. Please log in or register for full access.</p>
        <pre>{JSON.stringify(notes, null, 2)}</pre>
      </>
    );

  } else {
    // User is authenticated
    const { data: notes } = await supabase.from('notes').select()

    return <pre>{JSON.stringify(notes, null, 2)}</pre>
  }
}

