'use client';

import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import Link from 'next/link';
import { useEffect, useState } from 'react';

interface RegistrationWallModalProps {
  isOpen: boolean;
  onClose: () => void;
  message?: string;
}

export function RegistrationWallModal({ isOpen, onClose, message }: RegistrationWallModalProps) {
  const [showDialog, setShowDialog] = useState(isOpen);

  useEffect(() => {
    setShowDialog(isOpen);
  }, [isOpen]);

  const handleClose = () => {
    setShowDialog(false);
    onClose();
  };

  return (
    <Dialog open={showDialog} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Unlock Full Access</DialogTitle>
          <DialogDescription>
            {message || "You've reached your limit for free feature usage. Register or log in to continue enjoying all our features!"}
          </DialogDescription>
        </DialogHeader>
        <div className="grid gap-4 py-4">
          <Link href="/login" passHref>
            <Button className="w-full">Log In</Button>
          </Link>
          <Link href="/signup" passHref>
            <Button variant="outline" className="w-full">Register</Button>
          </Link>
        </div>
        <p className="text-sm text-center text-muted-foreground">
          Don't worry, it only takes a minute!
        </p>
      </DialogContent>
    </Dialog>
  );
}

import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom';
import Page from '@/app/notes/page'; // Adjust path as necessary
import { createServerClient } from '@/utils/supabase';
import { cookies } from 'next/headers';
import { GUEST_LIMIT, resetUsageCount } from '@/lib/guest-usage';

// Mock useRouter for navigation and cookies for server components
jest.mock('next/navigation', () => ({
  redirect: jest.fn(),
}));

jest.mock('next/headers', () => ({
  cookies: jest.fn(),
}));

jest.mock('@/utils/supabase', () => ({
  createServerClient: jest.fn(() => ({
    auth: {
      getUser: jest.fn(),
    },
    from: jest.fn(() => ({
      select: jest.fn(() => ({
        data: [], // Mock empty notes data for simplicity
      })),
    })),
  })),
}));


describe('Guest Access for Core Features', () => {
  const mockRedirect = require('next/navigation').redirect;
  const mockGetServerClient = createServerClient as jest.Mock;
  const mockGetUser = mockGetServerClient().auth.getUser as jest.Mock;
  const mockGetNotes = mockGetServerClient().from().select as jest.Mock;

  beforeEach(() => {
    localStorage.clear(); // Clear localStorage before each test
    resetUsageCount(); // Ensure guest usage is reset
    mockRedirect.mockClear();
    mockGetUser.mockClear();
    mockGetNotes.mockClear();
  });

  // Helper to simulate multiple guest uses
  const simulateGuestUses = (count: number) => {
    for (let i = 0; i < count; i++) {
      fireEvent.click(screen.getByText(/increment guest usage/i)); // Assuming an increment button exists for testing purposes
    }
  };

  it('should allow guest access for the first use and increment usage', async () => {
    mockGetUser.mockResolvedValue({ data: { user: null } }); // Simulate guest user
    mockGetNotes.mockResolvedValue({ data: [{ id: 1, title: 'Guest Note 1' }] });

    render(await Page());

    expect(screen.getByText(/Guest Mode - Limited Access/i)).toBeInTheDocument();
    expect(screen.getByText(/You have 1 out of 2 free uses./i)).toBeInTheDocument();
    expect(mockRedirect).not.toHaveBeenCalled();
  });

  it('should allow guest access up to the limit and increment usage', async () => {
    mockGetUser.mockResolvedValue({ data: { user: null } }); // Simulate guest user
    mockGetNotes.mockResolvedValue({ data: [{ id: 1, title: 'Guest Note 1' }] });

    // First use
    render(await Page());
    await waitFor(() => {
      expect(screen.getByText(/You have 1 out of 2 free uses./i)).toBeInTheDocument();
    });

    // Second use (re-render to simulate new access)
    render(await Page());
    await waitFor(() => {
      expect(screen.getByText(/You have 2 out of 2 free uses./i)).toBeInTheDocument();
    });

    expect(mockRedirect).not.toHaveBeenCalled();
  });


  it('should redirect guest user to login page when usage limit is reached', async () => {
    mockGetUser.mockResolvedValue({ data: { user: null } }); // Simulate guest user

    // Simulate usage up to the limit
    for (let i = 0; i < GUEST_LIMIT; i++) {
        localStorage.setItem('guest_feature_usage', (i + 1).toString()); // Direct manipulation for setup
    }

    render(await Page());

    // Because the limit is reached initially, the redirect should happen immediately
    await waitFor(() => {
      expect(mockRedirect).toHaveBeenCalledWith('/login');
    });
  });

  it('should not affect authenticated users by guest limit', async () => {
    mockGetUser.mockResolvedValue({ data: { user: { id: '123', email: 'test@example.com' } } }); // Simulate authenticated user
    mockGetNotes.mockResolvedValue({ data: [{ id: 1, title: 'Auth Note 1' }] });

    // Simulate guest usage exceeding limit (should not matter for authenticated user)
    localStorage.setItem('guest_feature_usage', (GUEST_LIMIT + 5).toString());

    render(await Page());

    expect(screen.queryByText(/Guest Mode - Limited Access/i)).not.toBeInTheDocument();
    expect(mockRedirect).not.toHaveBeenCalled();
    expect(screen.getByText(/Auth Note 1/i)).toBeInTheDocument(); // Expect authenticated content
  });
});

