import { render, screen, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom';
import Page from '@/app/notes/page'; // Adjust path as necessary
import { createServerClient } from '@/utils/supabase';
import { cookies } from 'next/headers';

// Mock useRouter for navigation and cookies for server components
jest.mock('next/navigation', () => ({
  redirect: jest.fn(),
}));

jest.mock('next/headers', () => ({
  cookies: jest.fn(),
}));

jest.mock('@/utils/supabase', () => ({
  createServerClient: jest.fn(() => ({
    auth: {
      getUser: jest.fn(() => Promise.resolve({ data: { user: null } })),
    },
    from: jest.fn(() => ({
      select: jest.fn(() => Promise.resolve({ data: [], error: null })), // Mock empty notes data for simplicity
    })),
  })),
}));

jest.mock('@/lib/guest-usage', () => ({
  getUsageCount: jest.fn(),
  incrementUsageCount: jest.fn(),
  checkUsageLimit: jest.fn(),
  resetUsageCount: jest.fn(),
  GUEST_LIMIT: 2,
}));


describe('Guest Access for Core Features', () => {
  const mockRedirect = require('next/navigation').redirect;
  const mockGetServerClient = createServerClient as jest.Mock;
  const mockGetUser = mockGetServerClient().auth.getUser as jest.Mock;
  const mockGetNotes = mockGetServerClient().from().select as jest.Mock;

  const mockGetUsageCount = require('@/lib/guest-usage').getUsageCount as jest.Mock;
  const mockIncrementUsageCount = require('@/lib/guest-usage').incrementUsageCount as jest.Mock;
  const mockCheckUsageLimit = require('@/lib/guest-usage').checkUsageLimit as jest.Mock;
  // const mockResetUsageCount = require('@/lib/guest-usage').resetUsageCount as jest.Mock; // Not directly used in tests

  beforeEach(() => {
    jest.clearAllMocks(); // Clear mocks before each test
    // Reset mocks for guest-usage
    mockGetUsageCount.mockReturnValue(0);
    mockIncrementUsageCount.mockImplementation(() => {
      const current = mockGetUsageCount();
      mockGetUsageCount.mockReturnValue(current + 1);
    });
    mockCheckUsageLimit.mockImplementation(() => mockGetUsageCount() >= 2);

    // Reset mocks for supabase
    mockGetUser.mockResolvedValue({ data: { user: null } });
    mockGetNotes.mockResolvedValue({ data: [], error: null });

    mockRedirect.mockClear();
  });

  it('should allow guest access for the first use and increment usage', async () => {
    render(await Page());

    expect(screen.getByText(/Guest Mode - Limited Access/i)).toBeInTheDocument();
    expect(screen.getByText(/You have 1 out of 2 free uses./i)).toBeInTheDocument();
    expect(mockGetUsageCount).toHaveBeenCalled();
    expect(mockIncrementUsageCount).toHaveBeenCalledTimes(1);
    expect(mockCheckUsageLimit).toHaveBeenCalled();
    expect(mockRedirect).not.toHaveBeenCalled();
  });

  it('should allow guest access up to the limit and increment usage', async () => {
    // First use
    render(await Page());
    await waitFor(() => {
      expect(screen.getByText(/You have 1 out of 2 free uses./i)).toBeInTheDocument();
      expect(mockIncrementUsageCount).toHaveBeenCalledTimes(1);
    });

    // Simulate second use (re-render component, so getUsageCount should return 1 initially)
    mockGetUsageCount.mockReturnValue(1); // Set state for the next render
    render(await Page());
    await waitFor(() => {
      expect(screen.getByText(/You have 2 out of 2 free uses./i)).toBeInTheDocument();
      expect(mockIncrementUsageCount).toHaveBeenCalledTimes(2); // Total calls across renders
    });

    expect(mockRedirect).not.toHaveBeenCalled();
  });


  it('should redirect guest user to login page when usage limit is reached', async () => {
    mockGetUsageCount.mockReturnValue(2); // Usage already at limit
    mockCheckUsageLimit.mockReturnValue(true); // Limit reached

    render(await Page());

    await waitFor(() => {
      expect(mockRedirect).toHaveBeenCalledWith('/login');
    });
  });

  it('should not affect authenticated users by guest limit', async () => {
    mockGetUser.mockResolvedValue({ data: { user: { id: '123', email: 'test@example.com' } } }); // Simulate authenticated user

    render(await Page());

    expect(screen.queryByText(/Guest Mode - Limited Access/i)).not.toBeInTheDocument();
    expect(mockGetUsageCount).not.toHaveBeenCalled(); // Guest usage functions should not be called
    expect(mockIncrementUsageCount).not.toHaveBeenCalled();
    expect(mockCheckUsageLimit).not.toHaveBeenCalled();
    expect(mockRedirect).not.toHaveBeenCalled();
    expect(screen.getByText(/{"data":\[\],"error":null}/i)).toBeInTheDocument(); // Expect output from authenticated path
  });
});

