'use client'

import React from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { QuestionCard } from './QuestionCard'
import { useQuizStore, QuizResults, Question } from '@/lib/quizStore'
import { Trophy, RotateCcw, Home, ChevronDown, ChevronUp } from 'lucide-react'
import { cn } from '@/lib/utils'

interface ResultsViewProps {
  onRetakeQuiz?: () => void
  onBackToDashboard?: () => void
}

export function ResultsView({ onRetakeQuiz, onBackToDashboard }: ResultsViewProps) {
  const { currentQuiz, results, userAnswers } = useQuizStore()
  const [showDetails, setShowDetails] = React.useState(false)

  if (!results) {
    return (
      <Card className="w-full max-w-2xl mx-auto">
        <CardContent className="p-8 text-center">
          <p>No results available</p>
        </CardContent>
      </Card>
    )
  }

  const getScoreColor = (percentage: number) => {
    if (percentage >= 80) return 'text-green-600'
    if (percentage >= 60) return 'text-yellow-600'
    return 'text-red-600'
  }

  const getScoreEmoji = (percentage: number) => {
    if (percentage === 100) return 'ðŸ†'
    if (percentage >= 80) return 'ðŸŽ‰'
    if (percentage >= 60) return 'ðŸ‘'
    if (percentage >= 40) return 'ðŸ“š'
    return 'ðŸ’ª'
  }

  const getScoreMessage = (percentage: number) => {
    if (percentage === 100) return 'Perfect score! Amazing!'
    if (percentage >= 80) return 'Great job! You really know this material!'
    if (percentage >= 60) return 'Good effort! Keep studying to improve.'
    if (percentage >= 40) return 'Not bad, but there\'s room for improvement.'
    return 'Keep studying! You\'ll get there!'
  }

  return (
    <div className="space-y-6">
      {/* Score Card */}
      <Card className="w-full max-w-2xl mx-auto">
        <CardHeader className="text-center pb-2">
          <div className="text-6xl mb-4">{getScoreEmoji(results.percentage)}</div>
          <CardTitle className="text-2xl">Quiz Complete!</CardTitle>
        </CardHeader>
        <CardContent className="text-center space-y-4">
          <div className="flex items-center justify-center gap-2">
            <Trophy className={cn('w-8 h-8', getScoreColor(results.percentage))} />
            <span className={cn('text-5xl font-bold', getScoreColor(results.percentage))}>
              {results.percentage}%
            </span>
          </div>
          
          <p className="text-lg text-muted-foreground">
            You got {results.score} out of {results.total} questions correct
          </p>
          
          <p className="text-muted-foreground">
            {getScoreMessage(results.percentage)}
          </p>

          {/* Action buttons */}
          <div className="flex gap-4 justify-center pt-4">
            {onRetakeQuiz && (
              <Button variant="outline" onClick={onRetakeQuiz}>
                <RotateCcw className="w-4 h-4 mr-2" />
                Retake Quiz
              </Button>
            )}
            {onBackToDashboard && (
              <Button onClick={onBackToDashboard}>
                <Home className="w-4 h-4 mr-2" />
                Back to Dashboard
              </Button>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Review toggle */}
      <div className="w-full max-w-2xl mx-auto">
        <Button
          variant="ghost"
          onClick={() => setShowDetails(!showDetails)}
          className="w-full justify-between"
        >
          <span>Review Answers</span>
          {showDetails ? (
            <ChevronUp className="w-4 h-4" />
          ) : (
            <ChevronDown className="w-4 h-4" />
          )}
        </Button>
      </div>

      {/* Detailed results */}
      {showDetails && currentQuiz?.questions && (
        <div className="space-y-4">
          {currentQuiz.questions.map((question, index) => {
            const result = results.results.find(
              (r) => r.question_id === question.id
            )
            
            return (
              <QuestionCard
                key={question.id}
                question={question}
                questionNumber={index + 1}
                totalQuestions={currentQuiz.questions!.length}
                selectedAnswer={userAnswers[question.id] || null}
                onAnswerSelect={() => {}} // Read-only
                showResult
                correctAnswer={result?.correct_answer}
                isCorrect={result?.is_correct}
              />
            )
          })}
        </div>
      )}
    </div>
  )
}

// frontend/__tests__/quizzes/quizzes.service.test.ts

import axios from 'axios'
import {
  generateQuiz,
  getQuiz,
  submitQuiz,
  getQuizzesForDocument,
} from '../../src/services/quizzes'

jest.mock('axios')
const mockedAxios = axios as jest.Mocked<typeof axios>

describe('Quizzes Service', () => {
  beforeEach(() => {
    jest.clearAllMocks()
  })

  describe('generateQuiz', () => {
    const mockQuizResponse = {
      data: {
        data: {
          id: 'quiz-123',
          document_id: 'doc-456',
          title: 'Test Quiz',
          status: 'ready',
          total_questions: 5,
          created_at: '2025-12-07T12:00:00Z',
        },
        message: 'Quiz generated successfully.',
        status: 'success',
      },
    }

    it('should generate a quiz successfully', async () => {
      mockedAxios.post.mockResolvedValueOnce(mockQuizResponse)

      const result = await generateQuiz('doc-456', 5)

      expect(mockedAxios.post).toHaveBeenCalledWith(
        '/api/v1/quizzes/generate',
        { document_id: 'doc-456', num_questions: 5 },
        { headers: {} }
      )
      expect(result.id).toBe('quiz-123')
      expect(result.status).toBe('ready')
    })

    it('should include authorization header when token provided', async () => {
      mockedAxios.post.mockResolvedValueOnce(mockQuizResponse)

      await generateQuiz('doc-456', 5, undefined, 'test-token')

      expect(mockedAxios.post).toHaveBeenCalledWith(
        '/api/v1/quizzes/generate',
        expect.any(Object),
        { headers: { Authorization: 'Bearer test-token' } }
      )
    })

    it('should throw error on failure', async () => {
      mockedAxios.post.mockRejectedValueOnce({
        isAxiosError: true,
        response: { data: { detail: 'Document not found' } },
      })
      mockedAxios.isAxiosError = jest.fn().mockReturnValue(true)

      await expect(generateQuiz('invalid-doc')).rejects.toThrow(
        'Failed to generate quiz: Document not found'
      )
    })
  })

  describe('getQuiz', () => {
    const mockQuizWithQuestions = {
      data: {
        id: 'quiz-123',
        document_id: 'doc-456',
        title: 'Test Quiz',
        status: 'ready',
        total_questions: 2,
        created_at: '2025-12-07T12:00:00Z',
        questions: [
          {
            id: 'q1',
            question_type: 'multiple_choice',
            question_text: 'What is 2 + 2?',
            options: ['A. 3', 'B. 4', 'C. 5', 'D. 6'],
            order_index: 0,
          },
        ],
      },
    }

    it('should fetch quiz with questions', async () => {
      mockedAxios.get.mockResolvedValueOnce(mockQuizWithQuestions)

      const result = await getQuiz('quiz-123')

      expect(mockedAxios.get).toHaveBeenCalledWith(
        '/api/v1/quizzes/quiz-123',
        { headers: {} }
      )
      expect(result.id).toBe('quiz-123')
      expect(result.questions).toHaveLength(1)
    })

    it('should throw error when quiz not found', async () => {
      mockedAxios.get.mockRejectedValueOnce({
        isAxiosError: true,
        response: { data: { detail: 'Quiz not found' } },
      })
      mockedAxios.isAxiosError = jest.fn().mockReturnValue(true)

      await expect(getQuiz('invalid-quiz')).rejects.toThrow(
        'Failed to get quiz: Quiz not found'
      )
    })
  })

  describe('submitQuiz', () => {
    const mockSubmitResponse = {
      data: {
        data: {
          quiz_id: 'quiz-123',
          score: 4,
          total: 5,
          percentage: 80,
          results: [
            { question_id: 'q1', is_correct: true, user_answer: 'B', correct_answer: 'B', explanation: null },
          ],
        },
        message: 'Quiz submitted successfully.',
        status: 'success',
      },
    }

    it('should submit quiz answers and return results', async () => {
      mockedAxios.post.mockResolvedValueOnce(mockSubmitResponse)

      const answers = { q1: 'B', q2: 'True' }
      const result = await submitQuiz('quiz-123', answers)

      expect(mockedAxios.post).toHaveBeenCalledWith(
        '/api/v1/quizzes/quiz-123/submit',
        {
          answers: [
            { question_id: 'q1', user_answer: 'B' },
            { question_id: 'q2', user_answer: 'True' },
          ],
        },
        { headers: {} }
      )
      expect(result.score).toBe(4)
      expect(result.percentage).toBe(80)
    })
  })

  describe('getQuizzesForDocument', () => {
    const mockQuizzesList = {
      data: [
        { id: 'quiz-1', document_id: 'doc-456', title: 'Quiz 1', status: 'ready', total_questions: 5 },
        { id: 'quiz-2', document_id: 'doc-456', title: 'Quiz 2', status: 'ready', total_questions: 10 },
      ],
    }

    it('should fetch all quizzes for a document', async () => {
      mockedAxios.get.mockResolvedValueOnce(mockQuizzesList)

      const result = await getQuizzesForDocument('doc-456')

      expect(mockedAxios.get).toHaveBeenCalledWith(
        '/api/v1/documents/doc-456/quizzes',
        { headers: {} }
      )
      expect(result).toHaveLength(2)
    })
  })
})
